import {Nav} from "react-bootstrap";
import * as React from "react";
import {useDispatch, useSelector} from "react-redux";
import {
    loadAllLocations,
    loadAllODSs, loadProjectsByFilter,
    loadProjectsByODS,
    selectAllLocations,
    selectAllODS
} from "../features/projects/projectSlice";
import {useEffect} from "react";

export default function filterByODS(filterType, {valueRef}){

    console.log(valueRef);
    const dispatch = useDispatch();
    const allODS = useSelector(selectAllODS);
    const allLocations = useSelector(selectAllLocations);
    let allCategories = '';
    let displayAllCategories = [];

    if(filterType.type.ods === true){allCategories = allODS}
    if(filterType.type.location === true){allCategories = allLocations}

    useEffect(()=>{
        dispatch(loadAllODSs())
        dispatch(loadAllLocations())
    },[]);

    function handleClickODSFilter(e){
        // dispatch(loadProjectsByODS(e.target.name));
        dispatch(loadProjectsByFilter(e.target.name,filterType.type));
        // cleanFromChild();
    }
    if(allCategories.length > 0 ){
        allCategories.forEach((o,index) => {
            // console.log(index);
            displayAllCategories.push(
                <Nav.Item key={index}>
                    <Nav.Link name={o} id={o} onChange={(e) => (valueRef.current = true)} eventKey="link-0" onClick={handleClickODSFilter}>{o}</Nav.Link>
                </Nav.Item>
            )
        })
    }else if(filterType.type !== true){
        return ;
        // <Nav.Item><p>No projects available for this filter</p></Nav.Item>;
    }
    return (
        <Nav className="justify-content-center" activeKey="/home">
            {
                displayAllCategories
            }
        </Nav>
    )
}
