import {Nav} from "react-bootstrap";
import * as React from "react";
import {useDispatch, useSelector} from "react-redux";
import {
    loadAllLocations,
    loadAllODSs,
    loadProjectsByODS,
    selectAllLocations,
    selectAllODS
} from "../features/projects/projectSlice";
import {useEffect} from "react";

export default function filterByODS(filterType){


    const dispatch = useDispatch();
    let allCategories = [];
    let displayAllCategories = [];

    console.log("filteryby ",filterType.type.ods);
        // else if(filterType === "location"){
    //     dispatch(loadAllLocations())
    //     allCategories = useSelector(selectAllLocations);
    // }
    if(filterType.type.ods === true){
        allCategories = useSelector(selectAllODS);
        console.log("filteryby ",filterType);
        dispatch(loadAllODSs())
    }


    useEffect(()=>{

    },[allCategories]);

    function handleClickODSFilter(e){
        console.log("inside filter",e.target.name);
        dispatch(loadProjectsByODS(e.target.name));

    }
    if(allCategories.length > 1){
        allCategories.forEach((o,index) => {
            // console.log(index);
            displayAllCategories.push(
                <Nav.Item key={index}>
                    <Nav.Link name={o} id={o} eventKey="link-0" onClick={handleClickODSFilter}>{o}</Nav.Link>
                </Nav.Item>
            )
        })
    }else{
        return <Nav.Item><p>No filters available</p></Nav.Item>;
    }
    return (
        <Nav className="justify-content-center" activeKey="/home">
            {
                displayAllCategories
            }
        </Nav>
    )
}
