import React, {useEffect, useState} from 'react';
import {ProjectNav} from "./ProjectNav";
import {useDispatch, useSelector} from "react-redux";
import {loadAllProjects,selectAllProjects} from "../../features/projects/projectSlice";
import ProjectsList from "../../views/Projects/ProjectsList";
import './projects_styles.scss';
import {Button, Grid} from "@mui/material";
import {Link} from "react-router-dom";

export function Projects(){
    const allProjects = useSelector(selectAllProjects);
    const dispatch = useDispatch();
    let display = {};
    const [newProjectForm, setNewProjectForm] = useState(false);


    useEffect(()=>{
        dispatch(loadAllProjects());
    },[])

    if(allProjects.length > 1){
        display =  <ProjectsList projects={allProjects}/>;
    }else{
        display = <p>No projects yet</p>;
    }
    function addNewProject(){
        setNewProjectForm(!newProjectForm);
        console.log("ddd",newProjectForm);
    }

    return (
        <div className="projects-container">
            <div className="grid-container">
                <Grid container>
                    <h2>Proyectos</h2>

                    <Grid item xs={12} ><ProjectNav/></Grid>
                    {
                    newProjectForm
                        ?
                        <>
                            <Link name="add-project" id="add-project" underline="hover" color="inherit" onClick={addNewProject}><Button color="error" variant="outlined">Cancelar</Button></Link>
                            <h2> FORM NEW PROJECT</h2>
                        </>
                        :
                        <>
                            <Link name="add-project" id="add-project" underline="hover" color="inherit" onClick={addNewProject}><Button color="success" variant="outlined">Agregar Proyecto</Button></Link>
                            <Grid item xs={12} >{display}</Grid>
                        </>

                    }
                </Grid>
            </div>
        </div>
    )
}
