import * as React from "react";
import {Nav} from "react-bootstrap";
import {useEffect, useRef, useState} from "react";
import FilterProjects from '../../services/FilterProjects.js'
import {useDispatch} from "react-redux";
import {loadAllProjects} from "../../features/projects/projectSlice";
import {Breadcrumbs} from "@mui/material";
import {Link} from "react-router-dom";

export function ProjectNav(){
    const valueRef = useRef(false);
    const dispatch = useDispatch();
    const [filter, setFilter] = useState({
        ods: false,
        location: false,
        needs: false
    });

    function handleClickFilters(e){
        setFilter({
            ...filter,
            [e.target.name]: true
        })
    }
    function cleanFilters() {
        setFilter({
            ods: false,
            location: false,
            needs: false
        });
        dispatch(loadAllProjects());
    }

    // Component Styles
    const styleNav = {
        display: 'flex',
        justifyContent: 'center',
        marginTop: 20,
        marginBottom: 20,
        backgroundColor: '#FFF0E2'
    };

    return (
        <section className="project-nav">
            <Breadcrumbs aria-label="breadcrumb" style={styleNav}>
                <Link underline="hover" color="inherit" name="ods" id="byODS"  onClick={handleClickFilters}>ODS</Link>
                <Link underline="hover" color="inherit" name="location" onClick={handleClickFilters}>Localidad</Link>
                <Link underline="hover" color="inherit" name="needs" id="byODS"  onClick={handleClickFilters}>Necesidades</Link>
                <Link underline="hover" color="inherit" name="clean" id="clean"  onClick={cleanFilters}>Borrar filtros</Link>
            </Breadcrumbs>
            <FilterProjects type={filter} valueRef={valueRef}/>
        </section>
    )
}
