import * as React from 'react';
import Card from 'react-bootstrap/Card';
import {Button, CardActions, CardContent, CardMedia, Typography} from "@mui/material";
import {useState} from "react";


export default function Project({project}){
    const [expand, setExpanded] = useState(false);
    const founders = ['Founders: '];
    const ods = ['ODS: '];

    if(project.ods !== ''){
        project.ods.forEach( o => {
            ods.push(o + ' - ');
        })
    }

    if(project.founders !== ''){
        project.founders.forEach( i => {
            founders.push(i + ' / ');
        })
    }
    function handleExpand(e){
        setExpanded(true);
        console.log("inside expand",e.target.name);

        if(expand){
            setExpanded(false);
            console.log("inside expand",expand);
        }

    }
    const styleExpanded = {
        width: '100vh',
        height: '100%'
    }

    return (
        <div>
            {
                expand
                ? <Card md={{height:100}}>
                    <CardMedia
                        md={{ height: 200}}
                        image={project.id}
                        title="green iguana"
                    />
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="div">{project.name}</Typography>
                        <Typography variant="body2" color="text.secondary">{project.description}</Typography>
                        <Typography>{ods}</Typography>
                        <Typography>{founders}</Typography>
                    </CardContent>
                    <CardActions>
                        <Button size="big" onClick={handleExpand}>Cerrar</Button>
                    </CardActions>
                </Card>
                : <Card sx={{ width: '18rem' }}>
                    <CardMedia
                        sx={{ height: 140 }}
                        image={project.id}
                        title={project.name}
                    />
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="div">{project.name}</Typography>
                        <Typography variant="body2" color="text.secondary">{project.description}</Typography>
                        <Typography>{ods}</Typography>
                        <Typography>{founders}</Typography>
                    </CardContent>
                    <CardActions>
                        <Button size="small" onClick={handleExpand}>Conocer</Button>
                    </CardActions>
                </Card>
            }
        </div>
    )
}
