import * as React from 'react';
import Card from 'react-bootstrap/Card';
import {Button, CardActions, CardContent, CardMedia, Typography} from "@mui/material";


export default function Project({project}){
    // console.log("final en project",project);
    const founders = ['Founders: '];
    const ods = ['ODS: '];

    if(project.ods !== ''){
        project.ods.forEach( o => {
            ods.push(o + ' - ');
        })
    }

    if(project.founders !== ''){
        project.founders.forEach( i => {
            founders.push(i + ' / ');
        })
    }
    function handleExpand(e){
        console.log("inside expand",e.target.name);
    }

    return (
        <Card sx={{ width: '18rem' }}>
            <CardMedia
                sx={{ height: 140 }}
                image={project.id}
                title="green iguana"
            />
            <CardContent>
                <Typography gutterBottom variant="h5" component="div">{project.name}</Typography>
                <Typography variant="body2" color="text.secondary">{project.description}</Typography>
                <Typography>{ods}</Typography>
                <Typography>{founders}</Typography>
            </CardContent>
            <CardActions>
                <Button size="small">Conocer</Button>
            </CardActions>
        </Card>
    )
}
