import * as React from 'react';
import Card from 'react-bootstrap/Card';
import {Button, CardActions, CardContent, CardMedia, Typography} from "@mui/material";


export default function Project({project}){
    // console.log("final en project",project);
    const founders = ['Founders: '];
    const ods = ['ODS: '];
    let expandToggle = false;

    if(project.ods !== ''){
        project.ods.forEach( o => {
            ods.push(o + ' - ');
        })
    }

    if(project.founders !== ''){
        project.founders.forEach( i => {
            founders.push(i + ' / ');
        })
    }
    function handleExpand(e){
        expandToggle = true;
        console.log("inside expand",e.target.name);

        if(expandToggle){
            expandToggle = false;
            console.log("inside expand",expandToggle);
        }

    }
    return (
        <div>
            {
                expandToggle ? <h1>works</h1>
                // ? <Card sx={{ width: '18rem' }}>
                //     <CardMedia
                //         sx={{ height: 140 }}
                //         image={project.id}
                //         title="green iguana"
                //     />
                //     <CardContent>
                //         <Typography gutterBottom variant="h5" component="div">{project.name}</Typography>
                //         <Typography variant="body2" color="text.secondary">{project.description}</Typography>
                //         <Typography>{ods}</Typography>
                //         <Typography>{founders}</Typography>
                //     </CardContent>
                //     <CardActions>
                //         <Button size="big" onClick={handleExpand}>Cerrar</Button>
                //     </CardActions>
                // </Card>
                : <Card sx={{ width: '18rem' }}>
                    <CardMedia
                        sx={{ height: 140 }}
                        image={project.id}
                        title={project.name}
                    />
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="div">{project.name}</Typography>
                        <Typography variant="body2" color="text.secondary">{project.description}</Typography>
                        <Typography>{ods}</Typography>
                        <Typography>{founders}</Typography>
                    </CardContent>
                    <CardActions>
                        <Button size="small" onClick={handleExpand}>Conocer</Button>
                    </CardActions>
                </Card>
            }
        </div>
    )
}
